import{_ as c,r as s,o as n,c as a,a as o,b as i,F as p,d as e,e as l}from"./app.3799c3e6.js";const r={},u=o("h1",{id:"why-use-composition-api",tabindex:"-1"},[o("a",{class:"header-anchor",href:"#why-use-composition-api","aria-hidden":"true"},"#"),e(" Why Use Composition API?")],-1),d=e("\uC6D0\uBB38: "),h={href:"https://github.com/vuejs/rfcs/issues/55#",target:"_blank",rel:"noopener noreferrer"},m=e("Function-based component API (extended discussion)"),b=l('<p><em>Composition API \uC81C\uC548\uC5D0 \uBC18\uB300\uD558\uB294 \uC720\uC800\uC758 \uAE00\uC5D0 \uB300\uD55C Evan You\uC758 \uB2F5\uBCC0 \uBC88\uC5ED</em></p><hr><p>\uC800\uB294 \uB2F9\uC2E0\uC758 \uC758\uACAC\uC744 \uC874\uC911\uD558\uBBC0\uB85C \uB2E4\uC74C \uB0B4\uC6A9\uC744 \uC5B4\uB290 \uAC83\uB3C4 \uAC1C\uC778\uC801\uC778 \uAC83\uC73C\uB85C \uBC1B\uC544\uB4E4\uC774\uC9C0 \uB9C8\uC138\uC694.</p><blockquote><p>\uACB0\uAD6D \uC815\uB9D0 \uC911\uC694\uD55C \uBB38\uC81C\uB4E4\uC740 \uC804\uD600 \uD574\uACB0\uB418\uC9C0 \uC54A\uAE30\uB54C\uBB38\uC5D0 \uC774 \uAE09\uC9C4\uC801\uC778 \uBCC0\uD654\uB294 \uC815\uB2F9\uD654\uD560 \uC218 \uC5C6\uC2B5\uB2C8\uB2E4. \uBCF8\uC9C8\uC801\uC73C\uB85C \uC774 \uC81C\uC548\uC740 \uADF8\uC800 \uAC89\uB9CC \uBC88\uC9C0\uB974\uB974\uD55C \uC0C8\uB85C\uC6B4 \uAC83\uC744 \uCAD3\uB294 \uAC83\uACFC \uAC19\uC2B5\uB2C8\uB2E4.</p></blockquote><p>\uC800\uB294 \uC774 \uB9D0\uC5D0 \uB3D9\uC758\uD560 \uC218 \uC5C6\uC2B5\uB2C8\uB2E4. Logic Composition\uC740 \uC544\uB9C8\uB3C4 \uD504\uB85C\uC81D\uD2B8 \uD655\uC7A5 \uCE21\uBA74\uC5D0\uC11C \uAC00\uC7A5 \uC911\uC694\uD55C \uBB38\uC81C \uC911 \uD558\uB098\uC77C \uAC83\uC785\uB2C8\uB2E4.\xA0\uB2E4\uB978 \uC2A4\uB808\uB4DC\uC5D0\uC11C \uC81C\uAC00 \uC37C\uB358 \uB9D0\uC744 \uB2E4\uC2DC \uC778\uC6A9\uD569\uB2C8\uB2E4:</p><ul><li><em>\uC774 RFC \uC2A4\uB808\uB4DC(Composition API \uC81C\uC548. \uC774\uD558 RFC)\uC5D0\uC11C\uB294 \uB9CE\uC740 \uC720\uC800\uB4E4\uC774 \uC815\uD655\uD788 \uBCF8\uC778\uB4E4\uC774 \uD604\uC7AC \uC9C1\uBA74\uD55C \uBB38\uC81C\uB97C \uD574\uACB0\uD574\uC904 \uC218 \uC788\uB2E4\uACE0 \uB9D0\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uADF8\uB9AC\uACE0 \uADF8\uB4E4\uC740 \uB610\uD55C \uD604\uC7AC\uC758 object-based syntax(Options API)\uAC00 \uD504\uB85C\uC81D\uD2B8\uC758 \uD655\uC7A5\uC131\uC744 \uBC29\uD574\uD558\uB294 \uBCD1\uBAA9 \uD604\uC0C1\uC774\uB77C\uACE0 \uC5B8\uAE09\uD588\uC2B5\uB2C8\uB2E4. \uB098\uB294 \uC5EC\uB7EC\uBD84 \uC911 \uB9CE\uC740 \uC0AC\uB78C\uB4E4\uC774 \uC774\uAC83\uC744 \uAC1C\uC778\uC801\uC73C\uB85C \uACBD\uD5D8\uD55C \uC801\uC774 \uC5C6\uB2E4\uB294 \uAC83\uC744 \uC778\uC815\uD569\uB2C8\uB2E4(\uC644\uC804\uD788 \uB9DE\uB294 \uB9D0\uC774\uACE0, \uC2EC\uC9C0\uC5B4 \uD6CC\uB96D\uD569\uB2C8\uB2E4!). \uADF8\uB7EC\uB098 \u2018\uBBF8\uD559\uC801\uC778\u2019 \uC120\uD0DD\uC774 \uC2E4\uC81C \uC720\uC9C0 \uAD00\uB9AC \uBD80\uB2F4\uC774 \uB41C\uB2E4\uBA74(\uBB3C\uB860 \uD2B9\uC815 \uC720\uD615\uC758 \uD504\uB85C\uC81D\uD2B8\uC5D0\uC11C\uB9CC), \uB354 \uC774\uC0C1 \uB2E8\uC21C\uD55C \uBB38\uC81C\uAC00 \uC544\uB2D9\uB2C8\uB2E4.\xA0\uB354 \uB098\uC740 \uCD94\uC0C1\uD654\uB85C \uBB38\uC81C\uB97C \uD574\uACB0\uD560 \uC218 \uC788\uB294 \uAE30\uD68C\uB97C \uC77C\uBD80 \uC0AC\uC6A9\uC790\uC758 \u2018\uBBF8\uC801\u2019 \uC120\uD638\uB85C \uC778\uD574 \uAC70\uBD80\uD558\uB294 \uAC83\uC740 \uC798\uBABB\uB41C \uC808\uCDA9\uC548\uCC98\uB7FC \uBCF4\uC785\uB2C8\uB2E4.</em></li></ul><p>Vue\uB294 \uC791\uAC8C \uC2DC\uC791\uD588\uC9C0\uB9CC \uC624\uB298\uB0A0\uC5D0\uB294 \uB2E4\uC591\uD55C \uC218\uC900\uC758 \uBCF5\uC7A1\uC131\uACFC \uBE44\uC988\uB2C8\uC2A4 \uC601\uC5ED\uC744 \uD3EC\uD568\uD558\uB294 \uB9E4\uC6B0 \uAD11\uBC94\uC704\uD55C \uD504\uB85C\uC81D\uD2B8\uC5D0\uC11C \uC0AC\uC6A9\uB418\uACE0 \uC788\uC2B5\uB2C8\uB2E4.\xA0\uB2E4\uC591\uD55C \uC720\uD615\uC758 \uD504\uB85C\uC81D\uD2B8\uB97C \uCC98\uB9AC\uD558\uB294 \uAC1C\uBC1C\uC790\uB294 \uB2E4\uC591\uD55C \uC694\uAD6C \uC0AC\uD56D\uC5D0 \uC9C1\uBA74\uD558\uAC8C \uB418\uBA70, \uC77C\uBD80\uB294 object-based syntax API\uB97C \uC0AC\uC6A9\uD558\uC5EC \uC27D\uAC8C \uCC98\uB9AC\uD560 \uC218 \uC788\uC9C0\uB9CC \uC77C\uBD80\uB294 \uADF8\uB807\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.\xA0\uC8FC\uC694 \uC608\uC2DC\uB4E4\uB85C</p><ol><li>\uC218\uBC31 \uC904 \uAE38\uC774\uC758, \uB2E4\uC591\uD55C \uB85C\uC9C1\uC744 \uD3EC\uD568\uD558\uACE0 \uC788\uB294 \uB300\uD615 \uCEF4\uD3EC\uB10C\uD2B8</li><li>\uC5EC\uB7EC \uCEF4\uD3EC\uB10C\uD2B8 \uAC04\uC5D0 \uACF5\uD1B5 \uB85C\uC9C1\uC744 \uACF5\uC720\uD560 \uD544\uC694\uC131</li></ol><p>(1) \uC5D0 \uD574\uB2F9\uD558\uB294 \uC0C1\uD669\uC5D0\uC11C\uB294 \uB85C\uC9C1\uB4E4\uC774 \uAC15\uC81C\uB85C \uAC01 \uC635\uC158 \uC720\uD615\uB4E4\uC5D0 \uB098\uB220\uC838 \uD560\uB2F9\uB429\uB2C8\uB2E4.\xA0\uC608\uB97C \uB4E4\uC5B4, \uD558\uB098\uC758 data fetching \uC791\uC5C5\uC774 <code>prop</code>, <code>data</code>, <code>computed property</code>, <code>mounted hook</code> \uADF8\uB9AC\uACE0 <code>watcher</code>\uAE4C\uC9C0 \uD544\uC694\uD560 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4.\xA0\uC989, \uCEF4\uD3EC\uB10C\uD2B8\uC5D0\uC11C \uD558\uB098\uC758 data fetching \uACFC\uC815\uC744 \uC774\uD574\uD558\uB824\uACE0 \uD560 \uB54C \uC635\uC158 \uBAA9\uB85D\uC5D0\uC11C \uACC4\uC18D \uC704\uC544\uB798\uB85C \uC810\uD504\uD558\uAC8C \uB429\uB2C8\uB2E4.\xA0\uB3D9\uC2DC\uC5D0 \uC18D\uC131\uC744 \uD6D1\uC5B4\uBCFC \uB54C \uC18D\uC131\uC774 \uC5B4\uB5A4\xA0<em>\uC720\uD615</em> \uC778\uC9C0 \uC54C\uACE0 \uC788\uC9C0\uB9CC \uCC98\uB9AC\uD574\uC57C\uD558\uB294\xA0<em>\uB85C\uC9C1</em> \uC744 \uB9D0\uD558\uAE30\uB294 \uC0C1\uB2F9\uD788 \uC5B4\uB835\uC2B5\uB2C8\uB2E4.\xA0\uCEF4\uD3EC\uB10C\uD2B8\uC5D0 \uB354 \uB9CE\uC740 \uB85C\uC9C1\uB4E4\uC774 \uCD94\uAC00\uB418\uBA74 \uC0C1\uD669\uC774 \uC545\uD654\uB429\uB2C8\uB2E4.\xA0\uC774\uC5D0 \uBE44\uD574 Composition API\uB97C \uC0AC\uC6A9\uD558\uBA74 \uD55C data fetching\uC744 \uC704\uD55C \uBAA8\uB4E0 \uAD00\uB828 \uB85C\uC9C1\uC744 \uADF8\uB8F9\uD654\uD560 \uC218 \uC788\uC73C\uBA70 \uB354 \uC911\uC694\uD55C \uAC83\uC740 <span class="content" style="font-weight:bold;">\uBCC4\uB3C4\uC758 \uD568\uC218 \uB610\uB294 \uBCC4\uB3C4\uC758 \uD30C\uC77C\uB85C \uAE54\uB054\uD558\uAC8C \uCD94\uCD9C\uD560 \uC218 \uC788\uB2E4\uB294 \uC810\uC785\uB2C8\uB2E4.</span></p><p>\uC774 \uBB38\uC81C\uB294 \uD504\uB85C\uC81D\uD2B8\uC758 \uD30C\uC77C \uAD6C\uC131\uC73C\uB85C \uBE44\uC720\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.\xA0\uC6B0\uB9AC \uC911 \uB9CE\uC740 \uC0AC\uB78C\uB4E4\uC774 \uD30C\uC77C \uC720\uD615\uBCC4\uB85C \uD30C\uC77C\uC744 \uAD6C\uC131\uD558\uB294 \uAC83(\uC608: \uBAA8\uB4E0 \uAC83\uC744\xA0<code>html</code>,\xA0<code>js</code>\uBC0F\xA0<code>css</code> \uD3F4\uB354\uB85C \uBD84\uD560)\uC774 \uD655\uC7A5\uC131\uC774 \uC5C6\uB2E4\uB294 \uB370 \uB3D9\uC758\uD569\uB2C8\uB2E4.\xA0\uD55C\uAC00\uC9C0 \uAE30\uB2A5\uACFC \uAD00\uB828\uB41C \uCF54\uB4DC\uB4E4\uC774 \uC798\uBABB \uC774\uD574\uD55C &quot;\uAD00\uC2EC\uC0AC \uBD84\uB9AC(seperation of concerns)&quot;\uB97C \uC704\uD574 \uC138 \uAC1C\uC758 \uD3F4\uB354\uB85C \uAC15\uC81C\uB85C \uBD84\uD560\uB429\uB2C8\uB2E4. \uC5EC\uAE30\uC11C \uD575\uC2EC\uC740 &quot;\uAD00\uC2EC\uC0AC&quot;\uAC00 \uD30C\uC77C \uD615\uC2DD\uC73C\uB85C \uC815\uC758\uB418\uC9C0 \uC54A\uB294\uB2E4\uB294 \uAC83\uC785\uB2C8\uB2E4.\xA0\uB300\uC2E0 \uC6B0\uB9AC \uB300\uBD80\uBD84\uC740 \uAE30\uB2A5\uC774\uB098 \uCC45\uC784(responsibility)\uBCC4\uB85C \uD30C\uC77C\uC744 \uAD6C\uC131\uD569\uB2C8\uB2E4. <strong>\uC774\uAC83\uC774 \uBC14\uB85C \uC0AC\uB78C\uB4E4\uC774 Vue \uB2E8\uC77C \uD30C\uC77C \uAD6C\uC131 \uC694\uC18C(SFC)\uB97C \uC88B\uC544\uD558\uB294 \uC774\uC720\uC785\uB2C8\uB2E4.</strong> SFC\uB294 \uAE30\uB2A5\uBCC4\uB85C \uCF54\uB4DC\uB97C \uAD6C\uC131\uD558\uB294 \uBC29\uBC95\uC785\uB2C8\uB2E4.\xA0\uC544\uC774\uB7EC\uB2C8\uD558\uAC8C\uB3C4 SFC\uAC00 \uCC98\uC74C \uB3C4\uC785\uB418\uC5C8\uC744 \uB54C \uB9CE\uC740 \uC0AC\uB78C\uB4E4\uC774 SFC\uAC00 \uAD00\uC2EC\uC0AC \uBD84\uB9AC\uC5D0 \uC704\uBC30\uB41C\uB2E4\uACE0 \uC0DD\uAC01\uD558\uC5EC \uBC18\uB300\uD588\uC9C0\uB9CC \uB098\uC911\uC5D0 SFC\uAC00 \uC2E4\uC81C\uB85C \uAD00\uC2EC\uC0AC\uB97C \uBD84\uB9AC\uD558\uB294 \uB354 \uD569\uB9AC\uC801\uC778 \uBC29\uBC95\uC774\uB77C\uB294 \uAC83\uC744 \uC778\uC815\uD588\uC2B5\uB2C8\uB2E4.</p><p>(2)\uB294 RFC\uC758 \u2018\uB3D9\uAE30 (Motivation)\u2019 \uC139\uC158\uC5D0\uC11C \uC911\uC694\uD558\uAC8C \uC124\uBA85\uD588\uB294\uB370, mixins/HOCs/scoped slots\uC774 \uD560 \uC218 \uC788\uB294 \uAC83\uB4E4\uC744 \uB2E8\uC810\uC5C6\uC774 \uB611\uAC19\uC774 \uD574\uB0BC \uC218 \uC788\uB2E4\uB294 \uAC83\uC744 \uBCF4\uC5EC\uC90D\uB2C8\uB2E4.</p><p>React Hook\uC744 \uD1B5\uD574 \uC6B0\uB9AC\uB294 Hook\uC758 \uD2B9\uC131 \uC911 \uC77C\uBD80\uAC00 \uC704\uC5D0\uC11C \uC5B8\uAE09\uD55C \uBB38\uC81C\uB4E4\uC744 \uD574\uACB0\uD558\uB294 \uB370 \uB3C4\uC6C0\uC774 \uB420 \uC218 \uC788\uC74C\uC744 \uBC1C\uACAC\uD588\uC2B5\uB2C8\uB2E4.\xA0\uC774\uAC83\uC774 \uC6B0\uB9AC\uAC00 \uC774 \uC81C\uC548(Composition API)\uC744 \uD558\uAC8C \uB41C \uADFC\uBCF8\uC801\uC778 \uC774\uC720\uC785\uB2C8\uB2E4. \uADF8\uC57C\uB9D0\uB85C &quot;\uC0C8\uB85C\uC6B4 \uAC83&quot;\uC774\uC9C0\uB9CC, \uC6B0\uB9AC\uB294 \uADF8\uAC83\uC774 &quot;\uC0C8\uB86D\uB2E4&quot;\uB294 \uC774\uC720\uAC00 \uC544\uB2C8\uB77C \uAC1D\uAD00\uC801\uC73C\uB85C \uC874\uC7AC\uD558\uB294 \uBB38\uC81C\uC5D0 \uB300\uD55C \uD574\uACB0\uCC45\uC744 \uC81C\uC2DC\uD558\uAE30 \uB54C\uBB38\uC5D0 \uC0C8\uB85C\uC6B4 \uAC83\uC744 \uCC44\uD0DD\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uC7A5\uAE30\uC801\uC73C\uB85C \uC774 \uC0C8\uB85C\uC6B4 API\uC758 \uAC00\uB2A5\uC131\uC774 \uC704\uC5D0\uC11C \uC5B8\uAE09\uD55C \uBB38\uC81C\uB4E4\uC744 \uB2E4\uB8E8\uB294 \uAC1C\uBC1C\uC790\uB4E4\uC758 \uC2DC\uAC04\uC744 \uC808\uC57D\uD574 \uB9C9\uB300\uD55C \uC774\uC775\uC744 \uAC00\uC838\uB2E4 \uC904 \uAC83\uC785\uB2C8\uB2E4.</p><p>Type Safety\uB3C4 \uC911\uC694\uD55C \uACE0\uB824 \uC0AC\uD56D\uC785\uB2C8\uB2E4. \uB2E4\uC2DC \uB9D0\uD558\uC9C0\uB9CC, \uC774 \uC0AC\uD56D \uB610\uD55C \uB9CE\uC740 \uC0AC\uC6A9\uC790\uB4E4\uC774\xA0<strong>\uAC04\uC808\uD788</strong>\xA0\uC6D0\uD588\uC9C0\uB9CC TypeScript\uB97C \uC0AC\uC6A9\uD558\uC9C0 \uC54A\uB294 \uC0AC\uB78C\uB4E4\uC5D0\uAC8C\uB294 \uAC00\uCE58\uAC00 \uC5C6\uC5B4 \uBCF4\uC77C \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uD574\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uADF8\uB7EC\uB098 \uD574\uACB0\uB418\uB294 \uBB38\uC81C\uAC00 \uB2F9\uC2E0\uC5D0\uAC8C \uC601\uD5A5\uC744 \uBBF8\uCE58\uC9C0 \uC54A\uAE30 \uB54C\uBB38\uC5D0 \uC5B4\uB5A4 \uBB38\uC81C\uB3C4 \uD574\uACB0\uB418\uC9C0 \uC54A\uB294\uB2E4\uACE0 \uC8FC\uC7A5\uD558\uB294 \uAC83\uC740 \uC57D\uAC04 \uC774\uAE30\uC801\uC778 \uAC83\uC774\uB77C\uACE0 \uC0DD\uAC01\uD569\uB2C8\uB2E4.</p><blockquote><p>\uBAA8\uB4E0 \uC885\uB958\uC758 \uD504\uB85C\uC81D\uD2B8\uC5D0 \uAE09\uC9C4\uC801\uC778 API \uBCC0\uACBD \uC0AC\uD56D\uC744 \uB3C4\uC785\uD558\uB294 \uAC83\uC740 \uAE09\uACA9\uD55C \uBCC0\uD654(Breaking Changes)\uC774\uBA70, \uC774\uC804 API\uC640\uC758 \uC5ED\uD638\uD658\uC131\uC744 \uAD00\uB300\uD558\uAC8C \uC81C\uACF5\uD574\uB3C4 \uC774 \uBB38\uC81C\uAC00 \uD574\uACB0\uB418\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.</p></blockquote><p>&quot;Breaking&quot;\uC740 \u2018\uC0AC\uC6A9\uC790\uAC00 \uAC15\uC81C\uB85C \uCF54\uB4DC\uB97C \uBCC0\uACBD\uD574\uC57C\uD568\u2019\uC73C\uB85C \uC815\uC758\uB429\uB2C8\uB2E4.\xA0\uC0AC\uC6A9\uC790\uB294 \uAE30\uC874 \uCF54\uB4DC\uB97C \uBCC0\uACBD\uD560 \uD544\uC694\uAC00 \uC5C6\uC73C\uBBC0\uB85C Breaking\uC774 \uC544\uB2D9\uB2C8\uB2E4.\xA0\uC774 \uBD80\uBD84\uC5D0 \uB300\uD574\uC11C\uB294 \uB354 \uC774\uC0C1 \uBC18\uBC15\uD560 \uC5EC\uC9C0\uAC00 \uC5C6\uB2E4\uACE0 \uC0DD\uAC01\uD569\uB2C8\uB2E4.</p><p>\uC774\uC804 \uBC84\uC804\uACFC\uC758 \uD638\uD658\uC131\uC73C\uB85C\uB3C4 \uCDA9\uBD84\uD558\uC9C0 \uC54A\uB2E4\uBA74, \uBCF8\uC9C8\uC801\uC73C\uB85C <strong>\uD504\uB85C\uC81D\uD2B8\uAC00 \uAE09\uC9C4\uC801\uC778 \uC0C8\uB85C\uC6B4 \uC544\uC774\uB514\uC5B4\uB97C \uB3C4\uC785\uD574\uC11C\uB294 \uC548 \uB41C\uB2E4\uACE0 \uB9D0\uD558\uB294 \uAC83\uC785\uB2C8\uB2E4.</strong>\xA0\uC800\uB294 \uC774\uAC83\uC774 \uD504\uB85C\uC81D\uD2B8 \uC815\uCC45 \uC218\uC900\uC758 \uB17C\uC7C1\uC774\uB77C\uACE0 \uC0DD\uAC01\uD569\uB2C8\uB2E4. \uB9CC\uC57D \uC81C\uAC00 \uD22C\uD45C\uB97C \uD55C\uB2E4\uBA74, \uC800\uB294 \uB2E8\uD638\uD558\uAC8C \uBC18\uB300\uD560 \uAC83\uC785\uB2C8\uB2E4.\xA0\uC6B0\uB9AC\uB294 \uC0AC\uC6A9\uC790\uC758 \uCD5C\uC120\uC758 \uC774\uC775\uC744 \uC5FC\uB450\uC5D0 \uB450\uACE0 \uCD5C\uC120\uC744 \uB2E4\uD560 \uAC83\uC774\uC9C0\uB9CC \uD504\uB85C\uC81D\uD2B8\uB294 \uBC1C\uC804\uD574\uC57C\uD558\uACE0 \uBC1C\uC804\uD560 \uAC83\uC785\uB2C8\uB2E4.</p><blockquote><p>\uC774\uB7F0 \uC2DD\uC73C\uB85C \uC77C\uC744 \uD558\uB294 \uAC83\uC740 \uBD88\uAC00\uC0AC\uC758\uD558\uACE0 \uBE44\uC794\uD2F4\uC774\uB098 \uBBF8\uB85C \uAC19\uC740 &quot;\uC2A4\uD30C\uAC8C\uD2F0 \uCF54\uB4DC&quot;\uB85C \uC774\uC5B4\uC9C8 \uAC00\uB2A5\uC131\uC774 \uB354 \uD07D\uB2C8\uB2E4.\xA0Vue\uC758 \uAC00\uC7A5 \uD070 \uC7A5\uC810 \uC911 \uD558\uB098\uB294 \uB2E8\uC21C\uD568\uACFC \uC811\uADFC\uC131\uC785\uB2C8\uB2E4.\xA0Vue \uC8FC\uC694 [\uBCC0\uB355\uC2A4\uB7FD\uACE0 \uC885\uC7A1\uC744 \uC218 \uC5C6\uB294 API \uBCC0\uACBD\uACFC \uC544\uD0A4\uD14D\uCC98 \uBB38\uC81C\uB85C \uC778\uD574 \uB2E4\uB978 \uB9CE\uC740 JavaScript \uAD00\uB828 \uC57D\uC18D\uC774 \uADF8\uB7EC\uD558\uB4EF\uC774 Vue\uC5D0 \uB300\uD55C \uC8FC\uC694 \uC870\uC9C1\uC758 \uC57D\uC18D\uC740 \uC190\uC5D0\uC11C \uC99D\uBC1C\uD558\uAE30\uBCF4\uB2E4\uB294 \uC720\uD1B5 \uAE30\uD55C\uACFC \uB0B4\uAD6C\uC131\uC744 \uAC00\uC9C8 \uB9CC\uD07C \uCDA9\uBD84\uD788 \uC815\uC801\uC785\uB2C8\uB2E4.]</p></blockquote><p>\uBC18\uB300\uB85C, \uC774 \uC81C\uC548\uC758 \uB3D9\uAE30\uB294 \uC7A5\uAE30\uC801\uC73C\uB85C Vue \uD504\uB85C\uC81D\uD2B8\uC758 \uC720\uC9C0\uBCF4\uC218\uC131\uC744 \uD5A5\uC0C1\uC2DC\uD0A4\uB294 \uAC83\uC774\uC5C8\uC2B5\uB2C8\uB2E4.</p><p>JavaScript \uD504\uB85C\uC81D\uD2B8\uB97C \uC0B4\uD3B4\uBCF4\uBA74 \uBAA8\uB4E0 \uCF54\uB4DC\uB294 \uD55C \uC2DC\uC791\uC9C0\uC810 \uD30C\uC77C\uC5D0\uC11C \uC2DC\uC791\uD558\uBA70, \uBCF8\uC9C8\uC801\uC73C\uB85C \uC554\uC2DC\uC801\uC73C\uB85C &quot;main&quot; \uD568\uC218\uAC00 \uD638\uCD9C\uB418\uBA74\uC11C \uC2DC\uC791\uD569\uB2C8\uB2E4.\xA0\uB2E8\uC77C \uD568\uC218 \uC2DC\uC791\uC810\uC774 \uC2A4\uD30C\uAC8C\uD2F0 \uCF54\uB4DC\uB85C \uC774\uC5B4\uC9C0\uB294 \uAC83\uC774\uB77C\uBA74 \uBAA8\uB4E0 JavaScript \uD504\uB85C\uC81D\uD2B8\uB294 \uC2A4\uD30C\uAC8C\uD2F0 \uCF54\uB4DC\uC5EC\uC57C \uD569\uB2C8\uB2E4. \uC774\uB294 \uBD84\uBA85\uD788 \uC0AC\uC2E4\uC774 \uC544\uB2D9\uB2C8\uB2E4.\xA0\uC65C\uB0D0\uAD6C\uC694?\xA0\uAC1C\uBC1C\uC790\uB85C\uC11C \uC6B0\uB9AC\uB294 \uCF54\uB4DC\uB97C \uBAA8\uB4C8 \uB610\uB294 \uB354 \uC791\uC740 \uAE30\uB2A5\uC73C\uB85C \uBD84\uD560\uD558\uC5EC \uAD6C\uC131\uD558\uB294 \uBC29\uBC95\uC744 \uBC30\uC6E0\uAE30 \uB54C\uBB38\uC785\uB2C8\uB2E4.</p><p>\uD568\uC218 \uAE30\uBC18 API \uB514\uC790\uC778\uC758 \uD575\uC2EC \uD2B9\uC9D5\uC740 <code>setup()</code> \uB0B4 \uCF54\uB4DC\uB97C \uC774\uD574\uD55C\uB2E4\uB294 \uAC83\uC774 \uAD00\uC6A9\uC801\uC778 JavaScript \uCF54\uB4DC\uB97C \uC774\uD574\uD558\uB294 \uAC83\uACFC \uB2E4\uB974\uC9C0 \uC54A\uC73C\uBA70, \uC77C\uBC18 JavaScript \uCF54\uB4DC\uB97C \uAD6C\uC131\uD558\uB294 \uB370 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uBAA8\uB4E0 \uAE30\uC220\uC744 \uC0AC\uC6A9\uD574 <code>setup()</code> \uD568\uC218\uB97C \uAD6C\uC131\uD560 \uC218 \uC788\uB2E4\uB294 \uAC83\uC785\uB2C8\uB2E4.\xA0\uD300\uC5D0\uC11C \uC77C\uBC18\uC801\uC778 JavaScript \uCF54\uB4DC\uC5D0 \uC0AC\uC6A9\uD558\uB294 \uBAA8\uB4E0 \uC9C0\uC2DD/\uC2A4\uD0C0\uC77C \uAC00\uC774\uB4DC/\uCF54\uB4DC \uAC80\uD1A0 \uD504\uB85C\uC138\uC2A4\uB97C Vue\xA0<code>setup()</code> \uD568\uC218\uC758 \uCF54\uB4DC\uC5D0 \uC801\uC6A9\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><p>\uC800\uB294 \uC0C8\uB85C\uC6B4 API\uAC00 <em>\uC774\uB860\uC801\uC73C\uB85C</em> \uCF54\uB4DC \uD488\uC9C8\uC5D0 \uB300\uD55C \uC784\uACC4\uAC12\uC774 \uB354 \uB0AE\uC744 \uC218 \uC788\uC74C\uC5D0 \uB3D9\uC758\uD569\uB2C8\uB2E4. \uADF8\uB7EC\uB098 \uC5B8\uAE09\uD55C \uBC14\uC640 \uAC19\uC774 Vue\uAC00 \uC544\uB2CC \uCF54\uB4DC\uBCA0\uC774\uC2A4\uC5D0\uC11C \uC2A4\uD30C\uAC8C\uD2F0 \uCF54\uB4DC\uB97C \uBC29\uC9C0\uD558\uAE30 \uC704\uD574 \uC774\uBBF8 \uC218\uD589\uD558\uACE0 \uC788\uB294 \uBAA8\uB4E0 \uC791\uC5C5\uB4E4\uC744 \uC801\uC6A9\uD560 \uC218 \uC788\uC73C\uBBC0\uB85C \uC644\uD654\uB420 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uBC18\uBA74\uC5D0 \uC0C8\uB85C\uC6B4 API\uB85C \uC791\uC131\uB41C \uCF54\uB4DC\uB294 \uCF54\uB4DC \uD488\uC9C8\uC758 \uC0C1\uD55C\uC120\uB3C4 \uC0C1\uB2F9\uD788 \uB192\uC2B5\uB2C8\uB2E4.\xA0\uC0C8\uB85C\uC6B4 API\uB85C \uC791\uC131\uB41C \uBAA8\uB4E0 \uCF54\uB4DC\uB294 \uC635\uC158 \uAE30\uBC18 API\uBCF4\uB2E4 \uD6E8\uC52C \uB354 \uB192\uC740 \uD488\uC9C8\uC758 \uCF54\uB4DC\uB85C \uB9AC\uD329\uD1A0\uB9C1\uB420 \uC218 \uC788\uC9C0\uB9CC \uC635\uC158 \uAE30\uBC18 API\uB97C \uC0AC\uC6A9\uD558\uBA74 \uBBF9\uC2A4\uC778\uC5D0 \uC758\uC874\uD574\uC57C\uD558\uACE0 \uADF8 \uB2E8\uC810\uB4E4\uB3C4 \uCC98\uB9AC\uD574\uC57C \uD569\uB2C8\uB2E4.</p><p>\uC800\uB294 \uB610\uD55C \uC774 RFC\uAC00 \uC720\uC9C0 \uBCF4\uC218\uC131\uC744 \uC704\uD574 \uB2E8\uC21C\uC131\uC744 \uAC70\uB798\uD558\uB294 \uAC83\uC5D0 \uAD00\uD55C \uAC83\uC774 \uC544\uB2C8\uB77C\uB294 \uC810\uC744 \uC9C0\uC801\uD558\uACE0 \uC2F6\uC2B5\uB2C8\uB2E4. \uC54C\uC544\uC57C\uD560 \uAC83\uC740 \uC9C0\uAE08\xA0\uBA87 \uB144 \uB3D9\uC548 \uC0AC\uC6A9\uD588\uC744 \uC218\uB3C4 \uC788\uB294 API\uC640 \uCC98\uC74C \uBCF8 API\uAC04\uC758 \uB290\uB08C\uC744 \uBE44\uAD50\uD558\uACE0 \uC788\uB2E4\uB294 \uC0AC\uC2E4\uC785\uB2C8\uB2E4.\xA0\uAE30\uBCF8\uC801\uC73C\uB85C \uC544\uB798\uAC00 \uCEF4\uD3EC\uB10C\uD2B8\uC5D0 \uAD00\uD55C \uAD00\uC810\uC758 \uC804\uD658\uC785\uB2C8\uB2E4.</p><ul><li>\uC635\uC158 \uAE30\uBC18 API\uB294 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uCEF4\uD3EC\uB10C\uD2B8\uAC00 \uD3EC\uD568\uD55C \uC18D\uC131/\uBA54\uC11C\uB4DC/\uC635\uC158\uC774 \uC815\uC758\uB41C \uC720\uD615\uC5D0 \uAE30\uBC18\uD574 \uC774\uD574\uD569\uB2C8\uB2E4.</li><li>\uD568\uC218 \uAE30\uBC18 API\uB294 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uCEA1\uC290\uD654\uD558\uB294 \uB17C\uB9AC\uC801 \uC8FC\uC81C\uC5D0 \uAE30\uBC18\uD574 \uC774\uD574\uD569\uB2C8\uB2E4.</li></ul><p>\uB9CE\uC740 \uC0AC\uC6A9\uC790\uAC00 &quot;\uB2E8\uC21C\uD568\uC744 \uC783\uB294\uB2E4&quot;\uACE0 \uB9D0\uD560 \uB54C \uD55C\uD0C4\uD558\uB294 \uAC83\uC740 \uC0AC\uC2E4 \uC635\uC158 \uC720\uD615\uBCC4\uB85C \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC0B4\uD544 \uC218 \uC788\uB294 \uB2A5\uB825\uC744 \uC783\uB294 \uAC83\uC5D0 \uB300\uD574 \uB9D0\uD558\uB294 \uAC83\uC785\uB2C8\uB2E4.\xA0<strong>\uADF8\uB7EC\uB098 \uC0C8 API\uB97C \uC0AC\uC6A9\uD558\uBA74 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC635\uC158 \uC720\uD615\uBCC4\uB85C \uC0B4\uD544 \uC218 \uC788\uB294 \uB2A5\uB825\uC744 \uC81C\uACF5\uD558\uB294 \uBD84\uC11D\uAE30\uB97C \uAD6C\uD604\uD558\uB294 \uAC83\uC774 \uB9E4\uC6B0 \uAC04\uB2E8\uD569\uB2C8\uB2E4.\xA0\uC989, \uC0C8 API\uC5D0\uC11C\uB294 \uB450 \uAC00\uC9C0 \uAD00\uC810 \uBAA8\uB450\uC5D0\uC11C \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC0B4\uD544 \uC218 \uC788\uC9C0\uB9CC \uC635\uC158 \uAE30\uBC18 API\uB97C \uC0AC\uC6A9\uD558\uBA74 \uD558\uB098\uB85C \uC81C\uD55C\uB429\uB2C8\uB2E4 (\uC635\uC158 \uAC04\uC5D0 \uBD84\uD560\uD560 \uB54C \uB17C\uB9AC\uC801 \uC8FC\uC81C\uC5D0 \uB300\uD55C \uC758\uB3C4\uAC00 \uC190\uC2E4\uB418\uAE30 \uB54C\uBB38\uC5D0).</strong></p><blockquote><p>Vue\uB97C \uC88B\uAC8C \uB9CC\uB4DC\uB294 \uAC83\uB4E4\uC744 \uBC84\uB9B4 \uAC00\uCE58\uAC00 \uC5C6\uC2B5\uB2C8\uB2E4.</p></blockquote><p>&quot;\uC544\uBB34\uAC83\uB3C4 \uBC84\uB9AC\uC9C0 \uC54A\uB294\uB2E4&quot;\uB294 \uB9D0\uC744 \uB418\uD480\uC774\uD558\uB294 \uAC83\uB3C4 \uC9C0\uACB9\uC2B5\uB2C8\uB2E4.\xA0\uADF8\uB7EC\uB098 &quot;Vue\uB97C \uC88B\uAC8C \uB9CC\uB4DC\uB294 \uAC83&quot;\uC774 \uC2E4\uC81C\uB85C \uBB34\uC5C7\uC778\uC9C0 \uC815\uC758\uD558\uB824\uACE0 \uD569\uB2C8\uB2E4.\xA0\uC774 RFC\uC5D0 \uBC18\uB300\uD558\uB294 \uB9CE\uC740 \uC0AC\uC6A9\uC790\uB294 \uADF8\uAC83\uC744 object-based syntax\uB85C \uC815\uC758\uD558\uACE0 object-base syntax\uB97C \uC81C\uAC70\uD558\uBA74 Vue\uB97C Vue\uC2A4\uB7FD\uAC8C \uB9CC\uB4DC\uB294 \uBAA8\uB4E0 \uAC83\uC744 \uC81C\uAC70\uD558\uB294 \uAC83\uCC98\uB7FC \uBCF4\uC778\uB2E4\uACE0 \uB9D0\uD569\uB2C8\uB2E4.\xA0\uADF8\uB7EC\uB098 \uADF8\uB300\uB85C \uB0A8\uC544 \uC788\uB294 \uAC83\uC744 \uC0B4\uD3B4\uBCF4\uACA0\uC2B5\uB2C8\uB2E4.</p><ul><li>\uD15C\uD50C\uB9BF \uAD6C\uBB38\uC740 \uBCC0\uACBD\uB418\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4 (\uC2EC\uC9C0\uC5B4 \uC131\uB2A5\uC774 \uD5A5\uC0C1\uB418\uACE0 \uC788\uC2B5\uB2C8\uB2E4!)</li><li>\uBC18\uC751\uC131 \uC2DC\uC2A4\uD15C\uC774 \uC791\uB3D9\uD558\uB294 \uBC29\uC2DD\uC740 \uBCC0\uACBD\uB418\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.</li><li>computed \uC18D\uC131, watcher \uBC0F \uCEF4\uD3EC\uB10C\uD2B8 life cycle\uC758 \uAC1C\uB150\uC740 \uBCC0\uACBD\uB418\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.</li><li>SFC \uD615\uC2DD\uC740 \uBCC0\uACBD\uB418\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.</li><li>CLI\uAC00 \uBCC0\uACBD\uB418\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.</li><li>\uD504\uB808\uC784\uC6CC\uD06C\uC758 \uC9C4\uBCF4\uC801 \uC131\uACA9\uC740 \uBCC0\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.</li><li>\uB354 \uB098\uC740 \uAC1C\uBC1C \uB3C4\uAD6C\uB97C \uC81C\uACF5\uD558\uACA0\uB2E4\uB294 \uD300\uC758 \uC57D\uC18D\uC740 \uBCC0\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.</li><li>\uC0AC\uC2E4 \uAE30\uC220\uC801\uC73C\uB85C, object format\uB3C4 \uBCC0\uACBD\uB418\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uC791\uB3D9\uD558\uB358 \uBAA8\uB4E0 \uAC83\uB4E4\uC740 \uACC4\uC18D \uC791\uB3D9\uD569\uB2C8\uB2E4.</li></ul><p>Vue\uC758 object-based syntax\uB294 \uCC98\uC74C\uBD80\uD130 \uC874\uC7AC\uD574\uC654\uC2B5\uB2C8\uB2E4.\xA0\uC704\uC758 \uB9CE\uC740 \uAC83\uB4E4\uC740 \uB098\uC911\uC5D0 \uCD94\uAC00\uB418\uC5C8\uC73C\uBA70 \uAC01\uAC01 Vue\uC758 \uC131\uC7A5\uC5D0 \uAE30\uC5EC\uD588\uC2B5\uB2C8\uB2E4.\xA0object-based syntax\uC774 \uB2F9\uC2E0\uC5D0\uAC8C \uC911\uC694\uD55C \uC804\uBD80\uB77C\uACE0 \uC0DD\uAC01\uD558\uC2E0\uB2E4\uBA74 \uD55C \uAC78\uC74C \uBB3C\uB7EC\uC11C\uC11C Vue\uB97C \uC2E4\uC81C\uB85C \uB9CC\uB4DC\uB294 \uAC83\uC774 \uBB34\uC5C7\uC778\uC9C0 \uB2E4\uC2DC \uC0DD\uAC01\uD574 \uBCF4\uC2DC\uAE30 \uBC14\uB78D\uB2C8\uB2E4.\xA0\uACB0\uAD6D \uC774 RFC\uB294 \uBCF4\uC774\uB294 \uAC83\uCC98\uB7FC \uAE09\uC9C4\uC801\uC778 \uBCC0\uD654\uAC00 \uC544\uB2D9\uB2C8\uB2E4.</p><h4 id="\u110E\u1161\u11B7\u1100\u1169\u1112\u1161\u11AF\u1106\u1161\u11AB\u1112\u1161\u11AB-\u1105\u1175\u11BC\u110F\u1173" tabindex="-1"><a class="header-anchor" href="#\u110E\u1161\u11B7\u1100\u1169\u1112\u1161\u11AF\u1106\u1161\u11AB\u1112\u1161\u11AB-\u1105\u1175\u11BC\u110F\u1173" aria-hidden="true">#</a> \uCC38\uACE0\uD560\uB9CC\uD55C \uB9C1\uD06C</h4>',29),_={href:"https://chodragon9.github.io/blog/composition-api-rfc",target:"_blank",rel:"noopener noreferrer"},I=e("Composition API RFC \uBC88\uC5ED");function q(A,P){const t=s("ExternalLinkIcon");return n(),a(p,null,[u,o("blockquote",null,[o("p",null,[d,o("a",h,[m,i(t)])])]),b,o("ul",null,[o("li",null,[o("a",_,[I,i(t)])])])],64)}var g=c(r,[["render",q]]);export{g as default};
